bool isSubsequence(String s, String t) {
  if (t.contains(s) || s.isEmpty) return true;
  if (t.isEmpty) return false;

  String output = "";
  int tSize = t.length;

  bool keepSeeking = true;

  int sPosAt = 0;
  int i = 0;

  while (keepSeeking) {
    if (t[i] == s[sPosAt]) {
      output += t[i];
      sPosAt++;
    }

    i++;
    keepSeeking = i < tSize && output.length < s.length;
  }

  return output == s;
}

void main() {
  String s = "leeeeetcode";
  String t =
      "";

  print(isSubsequence(s, t));
}
