func isSubsequence(_ s: String, _ t: String) -> Bool {
    if s.isEmpty {
        return true
    }

    if t.isEmpty {
        return false
    }

    var i = s.startIndex
    var j = t.startIndex

    while i < s.endIndex && j < t.endIndex {
        if s[i] == t[j] {
            i = s.index(after: i)
        }

        j = t.index(after: j)
    }

    return i == s.endIndex
}

let s: String = "leeeeetcode"
let t: String =
    ""

print(isSubsequence(s, t))
